mapper.update:
		update FACTURA
     	set 
     		  SERIE = #{record.serie}
     		, NUMAR = #{record.numar}
     		, DT_EMITERE = #{record.dtEmitere}
     		, DT_SCADENTA = #{record.dtScadenta}
     		, SUMA = #{record.suma}
     		, TVA = #{record.tva}
     		, TOTAL = #{record.total}
     		, DRAFT = #{record.draft}
     		, MEMO = #{record.memo}
		where 
			ID = #{record.id}
